/**
 * Create an new hammer instance
 * @function newHammer
 */
'use strict'

/** @lends newHammer */
function newHammer (node) {
  // Require here to avoid non-browser error
  const Hammer = require('hammerjs')
  let hammer = new Hammer(node, {
    cssProps: {
      userSelect: true
    }
  })
  return Object.assign(hammer, {Hammer})
}

export default newHammer
